    <div>
      <h1>Список задач</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}{" "}
      {/* Отображаем ошибку, если есть */}
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>{task.title}</li>
        ))}
      </ul>
      <button>Create Task</button>
      {isCreating ? (
        <div>
          <input type="text"/>
          <input />
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};

export default App;
